pub enum UrlGenerationError {

    /// Resource not found
    #[display(fmt = "Resource not found")]
    ResourceNotFound,

    /// Not all path pattern covered
    #[display(fmt = "Not all path pattern covered")]
    NotEnoughElements,

    /// URL parse error
    #[display(fmt = "{}", _0)]
    ParseError(UrlParseError),
}

pub enum UrlencodedError {

    /// Can not decode chunked transfer encoding
    #[display(fmt = "Can not decode chunked transfer encoding")]
    Chunked,

    /// Payload size is bigger than allowed. (default: 256kB)
    #[display(fmt =
              "Urlencoded payload size is bigger ({} bytes) than allowed (default: {} bytes)",
              size, limit)]
    Overflow {
        size: usize,
        limit: usize,
    },

    /// Payload size is now known
    #[display(fmt = "Payload size is now known")]
    UnknownLength,

    /// Content type error
    #[display(fmt = "Content type error")]
    ContentType,

    /// Parse error
    #[display(fmt = "Parse error")]
    Parse,

    /// Payload error
    #[display(fmt = "Error that occur during reading payload: {}", _0)]
    Payload(PayloadError),
}

pub enum JsonPayloadError {

    /// Payload size is bigger than allowed. (default: 32kB)
    #[display(fmt = "Json payload size is bigger than allowed")]
    Overflow,

    /// Content type error
    #[display(fmt = "Content type error")]
    ContentType,

    /// Deserialize error
    #[display(fmt = "Json deserialize error: {}", _0)]
    Deserialize(JsonError),

    /// Payload error
    #[display(fmt = "Error that occur during reading payload: {}", _0)]
    Payload(PayloadError),
}

pub enum PathError {

    /// Deserialize error
    #[display(fmt = "Path deserialize error: {}", _0)]
    Deserialize(de::Error),
}

pub enum QueryPayloadError {

    /// Deserialize error
    #[display(fmt = "Query deserialize error: {}", _0)]
    Deserialize(de::Error),
}

pub enum ReadlinesError {

    /// Error when decoding a line.
    /// Payload size is bigger than allowed. (default: 256kB)
    #[display(fmt = "Encoding error")]
    EncodingError,

    /// Payload error.
    #[display(fmt = "Error that occur during reading payload: {}", _0)]
    Payload(PayloadError),

    /// Line limit exceeded.
    #[display(fmt = "Line limit exceeded")]
    LimitOverflow,

    /// ContentType error.
    #[display(fmt = "Content-type error")]
    ContentTypeError(ContentTypeError),
}

pub enum ErrorHandlerResponse<B> {

    /// New http response got generated
    Response(ServiceResponse<B>),

    /// Result is a future that resolves to a new http response
    Future(Box<Future<Item = ServiceResponse<B>, Error =
               ServiceResponse<B>>>),
}

pub enum Either<A, B> {

    /// First branch of the type
    A(A),

    /// Second branch of the type
    B(B),
}

pub enum EitherResponder<A, B> where A: Future<Item = Response>,
         A::Error: Into<Error>, B: Future<Item = Response>,
         B::Error: Into<Error> {
    A(A),
    B(B),
}
